import './App.css';
import {useState} from 'react';
import axis from 'axios';
import axios from 'axios';
function App() {
const [ItemText, setItemText] = useState('')

const addItem = async () =>{
  e.preventDefault();
  try{
    const res = await axios.post('http://localhost:5500/api/item', {item : ItemText})
    console.log(res);
  }
  catch(err){
    console.log(err);
  }

}



  return (
    <div className="App">
      <h1>My Day's Must Do List</h1>
        <form className = "form" onSubmit={e => addItem(e)}>
          <input type = "text" placeholder= "Add your today's Must do Item" onChange={e => {setItemText(e.target.value)}} value ={ItemText}/>
          <button type = "submit"> Add </button>
        </form>
        <div className = "MustDoItemsList">
          <div className = "MustDoItem">
            <p className ="item-content">This is the item 1</p>
            <button className = "update-item">Update</button>
            <button className = "delete-item">Delete</button>
          </div>
          <div className = "MustDoItem">
            <p className ="item-content">This is the item 2</p>
            <button className = "update-item">Update</button>
            <button className = "delete-item">Delete</button>
          </div>
          <div className = "MustDoItem">
            <p className ="item-content">This is the item 3</p>
            <button className = "update-item">Update</button>
            <button className = "delete-item">Delete</button>
          </div>

    </div>

    </div>
  );
}

export default App;
